function configure_load_more(data_type, tag_id) {
	var load_more_page = 1;
	var load_url = "";
	var tag_params = "";
	if (data_type == "users" || data_type == "home"){
		load_url = "/users_load_more";
	}
	else {
		load_url = "/tags_load_more";
		tag_params = "&id="+tag_id;
	}
	
	var loading = false;
	$("#load_more_button").click(function(e) {
		loading = true;
		$("#load_more_button").hide();
		$('#load_more_button').after("<div id=load_more_spinner></div>");
		title = $(".date_section_title").last().text();
		var q;
		if (location.search == "") {
			q = "?page=" + load_more_page + "&title=" + title + tag_params;
		} else {
			q = location.search + "&page=" + load_more_page + "&title=" + title + tag_params;
		}
		
		if($("#user_id").length > 0){
			q += "&user_id=" + $('#user_id').attr('tag');
		}
		
		if($("#current_tab").length > 0){
			q += "&current_tab=" + $('#current_tab').attr('tag');
		}
		
		if (data_type == "users") {
			q += "&type=users"
		} else if (data_type == "home") {
			q += "&type=home"
		}
		
		$('<div/>').load(load_url + q, function(response, status, xhr){ 
			if(response == "false") {
				$('#load_more_spinner').remove();
				$('#load_more_button').after("<div id=load_more_spinner_last></div>");
			} else {
				$("#load_more_spinner").remove();
				$("#load_more_button").show();	
				$(this).appendTo('#wish_stream');    // once they're loaded, append them to our content area
				load_more_page += 1; //go to next page
				loading = false;
			}
		});	
		e.preventDefault();
	})
	
	function nearBottomOfPage() {
	    return $(window).scrollTop() > $(document).height() - $(window).height() - 200;
	}
	
	$(window).scroll(function(){
		if(loading) {
			return;
		}
       	if  (nearBottomOfPage()){
			$("#load_more_button").click();
		}
	});
}

