function add_invite_list(user_id, user_name, user_avatar){
	$("<a href='#' class='avatar_grid_small'><img alt='"+user_name+"' src='"+user_avatar+"'></a>")
	.attr("user_name", user_name).attr("user_id", user_id)
	.addClass("avatar").appendTo($("#receipients .list"));
}

function remove_invite_list(user_id){
	$('#recommend_friends .invite_grid[user_id='+user_id+']').removeClass("selected");
	$("#receipients .avatar[user_id="+user_id+"]").tipsy("hide");
	$("#receipients .avatar[user_id="+user_id+"]").remove();
}

function add_to_invited_users(invited_users){
	var selected = $('#receipients .list').children('a');
	$.each(selected, function(index, value){
		var user_id = $(value).attr("user_id");
		var user_name = $(value).attr("user_name");
		invited_users[user_id] = user_name;
	});
	return invited_users;
}

function invite_with_search(){
	var invited_users = {}
	var wish_id = $("#invite_wish_id").attr("wish_id");
	
	//add user from list to map invited_users
	invited_users = add_to_invited_users(invited_users);

	//input user name to invite
	$("#invite_search_bar").autocomplete({
		source: function(request, response){
			_gaq.push(['_trackEvent', 'search', 'ajax', request.term, request.term.length]);
			$.getJSON("/search_users", request, function(data) {
				response(data.map(function (value) {
					return {
						'label': value["name"],
						'value': value["name"],
						'id': value["id"],
						'url': "/users/" + value["id"],
						'type': "user",
						'avatar_url': value["avatar_url_static"]
					};  
				}));
			});
		},
		select: function(e, ui) {
			var user_id = ui.item.id;
			var user_name = ui.item.label
			var user_avatar = ui.item.avatar_url;
			if(!invited_users[user_id]) {
				invited_users[user_id] = user_name;
				add_invite_list(user_id, user_name, user_avatar);
			}
			$('#invite_search_bar').val("");
			return false;
		}
	}).hint()
	.data("autocomplete")._renderItem = function (ul, item) {
		//check the type and see how to render
		return $("<li class='search_item search_user'></li>")
		.data("item.autocomplete", item)
		.append("<a><div class='avatar_grid_small'><img src="+ item.avatar_url+"></img></div><div class='name'>" + item.label + "</div><div class='clr'></div></a>" )
		.appendTo(ul)
	};
	
	//click recommend invite user to invite
	$(".invite_grid").click(function(e) {
		var selected = $(this).hasClass("selected");
		if(selected) {
			var user_id = $(this).attr("user_id")
			remove_invite_list(user_id);
			delete invited_users[user_id];
		}
		else {
			var user_id = $(this).attr("user_id")
			var user_name = $(this).attr("user_name")
			var user_avatar = $(this).children('.avatar_grid_small').children('img').attr("src");
			if(!invited_users[user_id]) {
				invited_users[user_id] = user_name;
				add_invite_list(user_id, user_name, user_avatar);
			}
			$(this).toggleClass("selected");
		}
		e.preventDefault();
	});
	
	//click to invite all
	$('.invite_all').click(function (e){
		var users = $(this).parent().parent().children(".invite_grid");
		$.each(users, function (index, value){
			var user_id = $(value).attr("user_id")
			var user_name = $(value).attr("user_name")
			var user_avatar = $(value).children('.avatar_grid_small').children('img').attr("src");
			if(!invited_users[user_id]) {
				invited_users[user_id] = user_name;
				add_invite_list(user_id, user_name, user_avatar);
			}
			$(this).addClass("selected");
		});
		e.preventDefault();
	});
	
	//click to cancle invite
	$("#receipients .avatar").live('click', function() {
		var user_id = $(this).attr("user_id");
		remove_invite_list(user_id);
		delete invited_users[user_id];
	});
	
	//submit invite_user
	$('#send_invite_button').click(function(e) {
		e.preventDefault();
		if(Object.size(invited_users) == 0) {
			return;
		}
		$(this).parent().addClass('loading_spinner')
		$(this).hide()
		var text = $("textarea.invite_message").val();
		
		$.post(
			"/send_invites",
			{
				"users":invited_users,
				"text":text,
				"wish_id":wish_id
			},
			function(data) {
				$(".loading_spinner").removeClass('loading_spinner')				
				$("#email_invite_popup").remove();
			}				
		);
	});
}

