function autosize_dialog(){
	var browserHeight = document.documentElement.clientHeight;
	var titleHeight = $('.ui-dialog-titlebar').height();
	
	var contentHeight = $("#dialog-modal").children(':first').height();
	var docTop = $(document).scrollTop();
	if(contentHeight+titleHeight > browserHeight){
		$("#dialog-modal").height(browserHeight-titleHeight-50);
		$('.ui-dialog').css('top', docTop);
	}else{
		$("#dialog-modal").height(contentHeight+titleHeight);
		$('.ui-dialog').css('top', docTop+(browserHeight-contentHeight-titleHeight-50)/2);
	}
	//$("#dialog-modal").height()
}

function scroll_top(fadeMin, fadeSpeed){
	//set the link
	var el = $('#top-link');
	if (el){
		el.hide(); //in case the user forgot
		$(window).scroll(function() {
			if($(window).scrollTop() >= fadeMin){
				el.fadeIn(fadeSpeed);
			}
			else{
				el.fadeOut(fadeSpeed);
			}
		});
		//smoothscroll
		$('#top-link').click(function(e) {
			e.preventDefault();
			$('html,body').animate({
			    scrollTop:0
			},100);
		});
	}
}

